import { AntDesign, FontAwesome5 } from "@expo/vector-icons";
import { Button, StyleSheet } from "react-native";
import { Searchbar } from "react-native-paper";
import React, { useState } from "react";

import EditScreenInfo from "../components/EditScreenInfo";
import { Text, View } from "../components/Themed";
import { ScrollView } from "react-native";
import { Icon } from "react-native-paper/lib/typescript/components/Avatar/Avatar";
import { Pressable } from "react-native";

export default function TabThreeScreen() {
  const [onPress] = useState(true);
  if (onPress == true) {
    return (
      <View>
        <ScrollView horizontal={true}>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Playlist</Text>
          </View>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Music Artist</Text>
          </View>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Album</Text>
          </View>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Downloads</Text>
          </View>
        </ScrollView>
        <View style={{ flexDirection: "row" }}>
          <View style={{ flexDirection: "row" }}>
            <FontAwesome5
              name="retweet"
              size={20}
              color="white"
              style={styles.buttonstil}
            />
            <Text style={styles.ayaryazisi}>Recently Played</Text>
          </View>
          <Pressable
            onPress={() => onPress}
            style={({ pressed }) => ({
              opacity: pressed ? 0.5 : 1,
            })}
          >
            <FontAwesome5
              name="list"
              size={20}
              color="white"
              style={styles.buttonstil1}
            />
          </Pressable>
        </View>
      </View>
    );
  } else {
    return (
      <View>
        <ScrollView horizontal={true}>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Playlist</Text>
          </View>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Music Artist</Text>
          </View>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Album</Text>
          </View>
          <View style={styles.listcercevesi}>
            <Text style={styles.searchlist}>Downloads</Text>
          </View>
        </ScrollView>
        <View style={{ flexDirection: "row" }}>
          <View style={{ flexDirection: "row" }}>
            <FontAwesome5
              name="retweet"
              size={20}
              color="white"
              style={styles.buttonstil}
            />
            <Text style={styles.ayaryazisi}>Recently Played</Text>
          </View>
          <Pressable
            onPress={() => onPress}
            style={({ pressed }) => ({
              opacity: pressed ? 0.5 : 1,
            })}
          >
          </Pressable>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  buttonstil1: {
    marginTop: 25,
    marginLeft: 160,
  },
  buttonstil: {
    marginTop: 25,
    marginLeft: 10,
  },
  ayaryazisi: {
    marginTop: 25,
    marginLeft: 30,
  },
  listcercevesi: {
    marginLeft: 5,
    marginRight: 5,
    minwidth: 70,
    maxWidth: 120,
    height: 35,
    borderWidth: 1,
    justifyContent: "center",
    borderColor: "white",
    borderTopLeftRadius: 35 / 2,
    borderTopRightRadius: 35 / 2,
    borderBottomLeftRadius: 35 / 2,
    borderBottomRightRadius: 35 / 2,
  },
  searchlist: {
    marginLeft: 15,
    marginRight: 15,
    textAlign: "center",
    fontSize: 15,
    fontWeight: "400",
  },
});
