import * as WebBrowser from 'expo-web-browser';
import React, { useState } from "react";
import { StyleSheet, TouchableOpacity,Switch } from 'react-native';

import Colors from '../constants/Colors';
import { MonoText } from './StyledText';
import { Text, View } from './Themed';


export default function EditSettingInfo({ path }: { path: string }) {
  const [isEnabled, setIsEnabled] = useState(false);
  const toggleSwitch = () => setIsEnabled(previousState => !previousState);
  const [isEnabled1, setIsEnabled1] = useState(false);
  const toggleSwitch1 = () => setIsEnabled1(previousState => !previousState);
  const [isEnabled2, setIsEnabled2] = useState(false);
  const toggleSwitch2 = () => setIsEnabled2(previousState => !previousState);
  const [isEnabled3, setIsEnabled3] = useState(false);
  const toggleSwitch3 = () => setIsEnabled3(previousState => !previousState);
  const [isEnabled4, setIsEnabled4] = useState(false);
  const toggleSwitch4 = () => setIsEnabled4(previousState => !previousState);
  
  return (
    <View>
      <View>
        <Text style = {styles.baslik}>Data Saver</Text>
        <View style = {styles.ayarcercevesi}>
          <View style = {styles.ayaraltcercevesi}>
          <Text style= {styles.altbaslik}>Voice Quality</Text>
          <Text style={styles.yazi}>Set sound level to low</Text>  
          </View>
          <Switch
        trackColor={{ false: "#767577", true: "darkgreen" }}
        thumbColor={isEnabled ? "lightgreen" : "#f4f3f4"}
        onValueChange={toggleSwitch}
        value={isEnabled}/>
        </View>
      </View>
      <View>
      <Text style = {styles.baslik}>Video Podcastes</Text>
        <View style = {styles.ayarcercevesi}>
          <View style = {styles.ayaraltcercevesi}>
          <Text style= {styles.altbaslik}>Download Audio Only</Text>
          <Text style={styles.yazi}>Record video podcasts as audio only</Text>  
          </View>
          <Switch
        trackColor={{ false: "#767577", true: "darkgreen" }}
        thumbColor={isEnabled1 ? "lightgreen" : "#f4f3f4"}
        onValueChange={toggleSwitch1}
        value={isEnabled1}/>
        </View>
        <View style = {styles.ayarcercevesi}>
          <View style = {styles.ayaraltcercevesi}>
          <Text style= {styles.altbaslik}>Just listen to the sound</Text>
          <Text style={styles.yazi}>Play podcasts as audio while not connected to wifi</Text>  
          </View>
          <Switch
        trackColor={{ false: "#767577", true: "darkgreen" }}
        thumbColor={isEnabled2 ? "lightgreen" : "#f4f3f4"}
        onValueChange={toggleSwitch2}
        value={isEnabled2}/>
        </View>
      </View>
      <View>
        <Text style = {styles.baslik}>Play Music</Text>
        <View style = {styles.ayarcercevesi}>
          <View style = {styles.ayaraltcercevesi}>
          <Text style= {styles.altbaslik}>Offline Mode</Text>
          <Text style={styles.yazi}>When offline, you can only listen to downloaded music</Text>  
          </View>
          <Switch
        trackColor={{ false: "#767577", true: "darkgreen" }}
        thumbColor={isEnabled3 ? "lightgreen" : "#f4f3f4"}
        onValueChange={toggleSwitch3}
        value={isEnabled3}/>
        </View>
        <View style = {styles.ayarcercevesi}>
          <View style = {styles.ayaraltcercevesi}>
          <Text style= {styles.altbaslik}>Continuous</Text>
          <Text style={styles.yazi}>Allow to play one after the other</Text>  
          </View>
          <Switch
        trackColor={{ false: "#767577", true: "darkgreen" }}
        thumbColor={isEnabled4 ? "lightgreen" : "#f4f3f4"}
        onValueChange={toggleSwitch4}
        value={isEnabled4}/>
        </View>
      </View>
      
    </View>
  );
}
const styles = StyleSheet.create({
  ayarcercevesi:{
    marginLeft:15,
    marginBottom:15,
    flexDirection:'row'
  },
  ayaraltcercevesi:{
    height:50,
    width:280,
  },
  yazi:{
    color:'grey',
  },
  altbaslik:{
    fontSize:16,
  },
  baslik:{
    marginLeft:15,
    marginBottom:15,
    fontSize:15,
    fontWeight:'800',
    marginTop:20,
  },
});
